alert("welcome! to CALCULATOR press key :  OK.  for continue...");

const n1 = +prompt("Enter number: one", ""),
symboll = prompt("Enter (+) (-) (*) (/)", ""),
n2 = +prompt("Enter number: two", "");

let result;

switch (symboll) {
  case "+":
    result = n1 + n2;
  break;

  case "-":
    result = n1 - n2;
    break;

    case "*":
      result = n1 * n2;
      break;

  case "/":
    result = n1 / n2;
    break;
}

alert(`RESULT:  ${n1} ${symboll} ${n2} = ${result}`);


